    uploader.on('uploadSuccess', function (file,response) {
        var fileUrl = response.data.fileUrl;
        //TODO
        $("#responeseText").text("上传成功,文件名："+response.data.fileName);
    });

    // 当文件上传出错时触发
    uploader.on('uploadError', function (file) {
        $("#responeseText").text("上传失败");
    });

